<?phpfunction redirect_to($new_location) {	header("Location: " . $new_location);	exit;}function mysqli_prep($string) { // ESCAPE ALL STRINGS	global $link;	$escaped_string = mysqli_real_escape_string($link, $string);		return $escaped_string;}function confirm_query($result_set) {	global $link;	if (!$result_set) {		die("Database query failed: " . mysqli_error($link) . ".");	}}// START REP EMAIL SECTION FOR ALL FUNDRAISERS, CONTACTS, MEMBERS, AND CUSTOMERS// GET THE REP'S USER INFO IDfunction repID() {	global $link;	global $repEmail;		$query = "SELECT * FROM user_info WHERE email = '$repEmail'";	$result = mysqli_query($link, $query);	confirm_query($result);		$row = mysqli_fetch_assoc($result);	$repID = $row["userInfoID"];		return $repID;}// USE THE REP'S USER INFO ID TO GET THE REP'S GROUPS// START STEP 1: SELECT CATEGORYfunction select_category() {	global $link;		$output = '';	$output = $output . '	<select id="select_category" name="select_category[]" multiple="multiple" style="width: 276px;">		<option>All High Schools</option>		<option>All Middle Schools</option>		<option>All Elementary Schools</option>		<option>All Religious Organizations</option>		<option>All Youth &amp; Sports</option>		<option>All Local &amp; National Causes</option>	';		$repID = repID();		$query_setup = "SELECT * FROM Dealers WHERE setuppersonid = '$repID'";	$result_setup = mysqli_query($link, $query_setup);	confirm_query($result_setup);		if ($result_setup) {		while ($row_setup = mysqli_fetch_assoc($result_setup)) {			$school = $row_setup["Dealer"];			$club = $row_setup["DealerDir"];						if ($club = "") {				$output = $output . '<option>' . $school . '</option>';			}		}	}		$output = $output . '</select>';		return $output;}// END STEP 1: SELECT CATEGORY			// START STEP 2: SELECT GROUPfunction select_group() {	global $link;		$output = '';	$output = $output . '	<select id="select_group" name="select_group[]" multiple="multiple" style="width: 276px;">		<option>All Groups</option>		<option>All Clubs &amp; Organizations</option>		<option>All Athletics</option>		<option>General</option>	';		$query_clubTeam = "SELECT * FROM clubsTeams ORDER BY clubTeam ASC";	$result_clubTeam = mysqli_query($link, $query_clubTeam);	confirm_query($result_clubTeam);		if ($result_clubTeam) {		while ($row_clubTeam = mysqli_fetch_assoc($result_clubTeam)) {			$teams = $row_clubTeam["clubTeam"];						$output = $output . '<option>' . $teams . '</option>';		}	}		$output = $output . '</select>';		return $output;}// END STEP 2: SELECT GROUP			// START STEP 3: SELECT CONTACTfunction select_contact() {	global $link;		$output = '';	$output = $output . '	<select id="select_contact" name="select_contact" multiple="multiple" style="width: 276px;">		<option>All Contacts</option>		<option>Fundraising Leaders</option>		<option>Members</option>		<option>Consumers</option>	';		$repID = repID();		$query_setup = "SELECT * FROM Dealers WHERE setuppersonid = '$repID'";	$result_setup = mysqli_query($link, $query_setup);	confirm_query($result_setup);		if ($result_setup) {		while ($row_setup = mysqli_fetch_assoc($result_setup)) {			$group_loginid = $row_setup["loginid"];						// GET THE CONTACTS USING $group_loginid			$query_contacts = "SELECT * FROM orgContacts WHERE fundraiserID = '$group_loginid' ORDER BY orgFName ASC";			$result_contacts = mysqli_query($link, $query_contacts);			confirm_query($result_contacts);						if ($result_contacts) {				while ($row_contacts = mysqli_fetch_assoc($result_contacts)) {					$contact_first = $row_contacts["orgFName"];					$contact_last = $row_contacts["orgLName"];										$output = $output . '<option>' . $contact_first . " " . $contact_last . '</option>';				}			}						// GET THE MEMBERS USING $group_loginid			$query_members = "SELECT * FROM orgMembers WHERE fund_owner_id = '$group_loginid' ORDER BY orgFName ASC";			$result_members = mysqli_query($link, $query_members);			confirm_query($result_members);						if ($result_members) {				while ($row_members = mysqli_fetch_assoc($result_members)) {					$member_first = $row_members["orgFName"];					$member_last = $row_members["orgLName"];										$output = $output . '<option>' . $member_first . " " . $member_last . '</option>';				}			}						// GET THE CUSTOMERS USING $group_loginid			$query_customers = "SELECT * FROM orgCustomers WHERE fundGroupID = '$group_loginid' ORDER BY first ASC";			$result_customers = mysqli_query($link, $query_customers);			confirm_query($result_customers);						if ($result_customers) {				while ($row_customers = mysqli_fetch_assoc($result_customers)) {					$customer_first = $row_customers["first"];					$customer_last = $row_customers["last"];										$output = $output . '<option>' . $customer_first . " " . $customer_last . '</option>';				}			}		}	}		$output = $output . '</select>';		return $output;	}// END STEP 3: SELECT CONTACT// END REP EMAIL SECTION FOR ALL FUNDRAISERS, CONTACTS, MEMBERS, AND CUSTOMERS?>